var booking=document.querySelectorAll(".button-booking");var popupBooking=document.querySelector(".popup-product-in-basket");var close=document.querySelectorAll(".close-popup");booking.forEach(function(a){a.addEventListener("click",function(b){b.preventDefault();popupBooking.classList.add("popup-show")})});close.forEach(function(a){a.addEventListener("click",function(b){b.preventDefault();popupBooking.classList.remove("popup-show")})});window.addEventListener("keydown",function(a){if(a.keyCode===27){if(popupBooking.classList.contains("popup-show")){a.preventDefault();popupBooking.classList.remove("popup-show")}}});