var booking=document.querySelectorAll(".button-booking");var popupBooking=document.querySelector(".popup-product-in-basket");var close=document.querySelectorAll(".close-popup");var map=document.querySelector(".map");var popupMap=document.querySelector(".map-popup");var message=document.querySelector(".contact .button");var popupMessage=document.querySelector(".popup-message");var messageName=popupMessage.querySelector("#message-name");var messageMail=popupMessage.querySelector("#message-mail");var messageText=popupMessage.querySelector("#message-text");var form=popupMessage.querySelector("form");booking.forEach(function(a){a.addEventListener("click",function(b){b.preventDefault();popupBooking.classList.add("popup-show")})});map.addEventListener("click",function(a){a.preventDefault();popupMap.classList.add("popup-show")});message.addEventListener("click",function(a){a.preventDefault();popupMessage.classList.add("popup-show");messageName.focus()});form.addEventListener("submit",function(a){if(!messageName.value||!messageMail.value||!messageText.value){a.preventDefault();popupMessage.classList.remove("popup-error");popupMessage.offsetWidth=popupMessage.offsetWidth;popupMessage.classList.add("popup-error")}});close.forEach(function(a){a.addEventListener("click",function(b){b.preventDefault();popupBooking.classList.remove("popup-show");popupMap.classList.remove("popup-show");popupMessage.classList.remove("popup-show");popupMessage.classList.remove("popup-error")})});window.addEventListener("keydown",function(a){if(a.keyCode===27){if(popupMap.classList.contains("popup-show")){a.preventDefault();popupMap.classList.remove("popup-show")}if(popupBooking.classList.contains("popup-show")){a.preventDefault();popupBooking.classList.remove("popup-show")}if(popupMessage.classList.contains("popup-show")){a.preventDefault();popupMessage.classList.remove("popup-show");popupMessage.classList.remove("popup-error")}}});